open Core

type t = {
  position: int * int;
  size: int;
  used: int;
  avail: int;
} [@@deriving fields, sexp]

let make position size used avail = Fields.create ~position ~size ~used ~avail

module Parser = struct
  open Angstrom
  let make_twople a b = (a, b)
  let whitespace = many (char ' ')
  let integer = take_while1 (function '0' .. '9' -> true | _ -> false) >>| Int.of_string
  let position = make_twople <$> (char 'x' *> integer) <*> (char '-' *> char 'y' *> integer)
  let file_path = string "/dev/grid/node-" *> position
  let file_size = integer <* char 'T'
  let percentage = integer <* char '%'

  let node = lift4 make 
      (whitespace *> file_path) 
      (whitespace *> file_size) 
      (whitespace *> file_size) 
      (whitespace *> file_size) <*
             (whitespace *> percentage)

  let parse str = 
    parse_string (sep_by (char '\n') node) str
    |> Result.ok_or_failwith

  let%expect_test _ =
    parse {|/dev/grid/node-x0-y2     94T   72T    22T   76%
            /dev/grid/node-x0-y3     85T   69T    16T   81%|}
    |> [%sexp_of : t list]
    |> print_s;
    [%expect {|
      (((position (0 2)) (size 94) (used 72) (avail 22))
       ((position (0 3)) (size 85) (used 69) (avail 16))) |}]
end

let is_viable a b = 
  a.used <> 0 &&
  a.position <> b.position &&
  a.used <= b.avail

let viable_nodes_for node nodes = 
  List.fold_left nodes ~init:[] ~f:(fun pairs current_node -> 
      if is_viable node current_node then 
        ((node, current_node) :: pairs) 
      else if is_viable current_node node then
        ((current_node, node) :: pairs)       
      else 
        pairs
    )

let rec node_pairs nodes = match nodes with 
  | [] -> []
  | n :: ns -> (viable_nodes_for n ns) :: node_pairs ns

let%expect_test "part one" =
  In_channel.read_all "./df.txt" 
  |> Parser.parse
  |> node_pairs
  |> List.concat  
  |> List.length |> [%sexp_of : int]  
  |> print_s;
  [%expect {| 924 |}]

let pad_start chr min_len str = (* require('left-pad) *)
  if String.length str >= min_len then str else (
    String.concat [String.make (min_len - String.length str) chr; str]
  )

let%expect_test _ =
  pad_start 'x' 5 "123" 
  |> print_endline;
  [%expect {| xx123 |}]

let pos_to_string (x, y) = String.concat ["(";Int.to_string x; ","; Int.to_string y;")"] |> pad_start ' ' 7

let to_string {position;used;size;_} = 
  if size > 100 then "--/--" else
    String.concat [
      pos_to_string position; 
      " ";
      pad_start ' ' 6 (String.concat [
          (if used = 0 then "**" else Int.to_string used); 
          "/"; 
          (Int.to_string size);
        ]);
    ]


let grid_to_string w nodes = 
  List.groupi nodes ~break:(fun i _ _ -> i % w = 0)
  |> List.map ~f:(fun row ->String.concat ~sep:" " (List.map row ~f:to_string))

